				var target = $(e.target);
				if(!target) return;
				if(!target.hasClass('popCont') && !target.is($('.popCont').children())){
					$scope.toggleMenu();
				}
			};
		} else if (!e) {
			//$('#top *').attr('disabled', false);
			$window.onclick = null;
			$scope.$apply();
		}
	}
	$scope.undo = function(e) {
