		//$scope.rowCopy = $scope.rows;
		if (typeof $scope.listener != 'undefined')
			$scope.listener();
		$scope.pageModified = false;
		$scope.showSpin = true;
		//$('#top *').attr('disabled', true);
		var sib = $(e.target).prev();
		$scope.editLabel = 'Edit Page Layout';
		$(sib).toggleClass('btn-primary');
