					tmpOb.id = input.id;
					tmpOb.options = input.widgOptions;
					tmpOb.widgetOb = $sp.getWidget(input.id, input.widgOptions);
					data.userRows = userRows;
				}
			}
			else if (input.runThis == 'lookup') {
